import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-users',
  templateUrl: './users.component.html',
  styleUrls: ['./users.component.css']
})
export class UsersComponent implements OnInit {

  constructor() { }

  ngOnInit(): void {
  }

  // func alert
  display(){
    alert('hello')
  }

  // define user data
  users = [
    {"id":1,"name":"Tran Hoang Hiep",'age':"32","phone":"012312313",'avatar':"data:image/jpeg;base64,"},
    {"id":2,"name":"Tran Hoang bla bla",'age':"32","phone":"012312313",'avatar':"data:image/jpeg;base64,"},
    {"id":3,"name":"Tran Hoang Hiep",'age':"32","phone":"012312313",'avatar':"data:image/jpeg;base64,"}
  ];
   arrUserNew = this.users;


  // action remove (filter)
  remove(id?:number) {
    // this.users.forEach((val,index)=>{
    //   if(val.id == id){
    //     // remove
    //     this.users.splice(index,1);
    //   }
    // });
    this.arrUserNew = this.users.filter((val,index)=>{
      return val.id !== id;
    });

  }

  // action seacrch
  // even: any : moi loai kieu du lieu
  search(event:any){
    // convert to lowercase 
    const value = event.target.value.toLowerCase().trim();
    
    // convert to unicode 

      this.arrUserNew = this.users.filter((val,index)=>{
      let nameLowerCase = val.name.toLowerCase();
      // value default indexOf return = -1 neu nhu dk sai;
      return nameLowerCase.indexOf(value) !== -1;

    });  
    
 
  }


}
